client/src/contexts/AuthContext.tsx.content:
import { createContext, useContext, useState, useEffect, ReactNode } from "react";
import { auth, onAuthChange, signOut as firebaseSignOut, type FirebaseUser } from "@/lib/firebase";
import { apiRequest } from "@/lib/queryClient";
import type { User } from "@shared/schema";

interface AuthContextType {
  firebaseUser: FirebaseUser | null;
  user: User | null;
  loading: boolean;
  signOut: () => Promise<void>;
  checkSessionAuth: () => Promise<boolean>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: ReactNode }) {
  const [firebaseUser, setFirebaseUser] = useState<FirebaseUser | null>(null);
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);

  const checkSessionAuth = async () => {
    try {
      // Check if we have a session-based authentication
      const response = await fetch('/api/dashboard', {
        method: 'GET',
        credentials: 'include'
      });

      if (response.ok) {
        const userData = await response.json();
        setUser(userData);
        setFirebaseUser(null); // Clear Firebase user since we're using session
        return true;
      }
    } catch (error) {
      console.log("No session authentication found");
    }
    return false;
  };

  useEffect(() => {
    const initializeAuth = async () => {
      // First check for session-based authentication
      const hasSession = await checkSessionAuth();

      if (!hasSession) {
        // Fall back to Firebase authentication
        const unsubscribe = onAuthChange(async (fbUser) => {
          setFirebaseUser(fbUser);

          if (fbUser) {
            try {
              const userData = await apiRequest<User>("POST", "/api/auth/verify", { 
                email: fbUser.email,
                name: fbUser.displayName || fbUser.email?.split("@")[0] || "User",
                firebaseUid: fbUser.uid,
              });
              setUser(userData);
            } catch (error) {
              console.error("Error verifying user:", error);
              setUser(null);
            }
          } else {
            setUser(null);
          }

          setLoading(false);
        });

        return () => unsubscribe();
      } else {
        setLoading(false);
      }
    };

    initializeAuth();
  }, []);

  const signOut = async () => {
    try {
      // Try session logout first
      await fetch('/api/auth/logout', {
        method: 'POST',
        credentials: 'include'
      });
    } catch (error) {
      console.log("Session logout failed, trying Firebase logout");
    }

    // Also try Firebase logout
    await firebaseSignOut();
    setUser(null);
    setFirebaseUser(null);
  };

  return (
    <AuthContext.Provider value={{ firebaseUser, user, loading, signOut, checkSessionAuth }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
}

client/src/lib/firebase.ts.content:
import { initializeApp, type FirebaseApp } from "firebase/app";
import { getAuth, GoogleAuthProvider, signInWithPopup, signInWithEmailAndPassword, createUserWithEmailAndPassword, signOut as firebaseSignOut, onAuthStateChanged, User as FirebaseUser, type Auth } from "firebase/auth";

const firebaseConfig = {
  apiKey: import.meta.env.VITE_FIREBASE_API_KEY || "demo-api-key",
  authDomain: `${import.meta.env.VITE_FIREBASE_PROJECT_ID || "demo-project"}.firebaseapp.com`,
  projectId: import.meta.env.VITE_FIREBASE_PROJECT_ID || "demo-project",
  storageBucket: `${import.meta.env.VITE_FIREBASE_PROJECT_ID || "demo-project"}.firebasestorage.app`,
  appId: import.meta.env.VITE_FIREBASE_APP_ID || "demo-app-id",
};

// Check if Firebase is properly configured
const isFirebaseConfigured = import.meta.env.VITE_FIREBASE_API_KEY && 
  import.meta.env.VITE_FIREBASE_API_KEY !== "demo-api-key" &&
  import.meta.env.VITE_FIREBASE_PROJECT_ID &&
  import.meta.env.VITE_FIREBASE_PROJECT_ID !== "demo-project" &&
  import.meta.env.VITE_FIREBASE_APP_ID &&
  import.meta.env.VITE_FIREBASE_APP_ID !== "demo-app-id";

let app: FirebaseApp | null = null;
let auth: Auth | null = null;

if (isFirebaseConfigured) {
  try {
    app = initializeApp(firebaseConfig);
    auth = getAuth(app);
  } catch (error) {
    console.error("Failed to initialize Firebase:", error);
    // Create a mock auth object for development
    auth = null;
  }
} else {
  console.warn("Firebase not properly configured. Using mock authentication for development.");
  auth = null;
}

export { auth };

const googleProvider = new GoogleAuthProvider();

export const signInWithGoogle = async () => {
  if (!auth) {
    throw new Error("Firebase authentication is not configured. Please configure your Firebase credentials.");
  }
  return signInWithPopup(auth, googleProvider);
};

export const signInWithEmail = (email: string, password: string) => {
  if (!auth) {
    throw new Error("Firebase authentication is not configured. Please configure your Firebase credentials.");
  }
  return signInWithEmailAndPassword(auth, email, password);
};

export const signUpWithEmail = (email: string, password: string) => {
  if (!auth) {
    throw new Error("Firebase authentication is not configured. Please configure your Firebase credentials.");
  }
  return createUserWithEmailAndPassword(auth, email, password);
};

export const signOut = async () => {
  if (!auth) {
    return Promise.resolve();
  }
  return firebaseSignOut(auth);
};

export const onAuthChange = (callback: (user: FirebaseUser | null) => void) => {
  if (!auth) {
    // Call callback with null user immediately if Firebase is not configured
    callback(null);
    return () => {}; // Return empty unsubscribe function
  }
  return onAuthStateChanged(auth, callback);
};

export type { FirebaseUser };

